CREATE OR REPLACE PROCEDURE proj_atualizar_subproj_etapa ( nr_seq_subproj_p bigint, nm_usuario_p text) AS $body$
DECLARE



nr_seq_cronograma_w	bigint;


BEGIN


select	max(nr_sequencia)
into STRICT	nr_seq_cronograma_w
from	proj_cronograma
where	nr_seq_proj		= nr_seq_subproj_p
and	ie_tipo_cronograma 	= 'E'
and	coalesce(ie_situacao,'A')	= 'A';

if (nr_seq_subproj_p IS NOT NULL AND nr_seq_subproj_p::text <> '') then
	CALL proj_atualizar_subprojeto(nr_seq_cronograma_w,nm_usuario_p);
end if;

commit;

end;
