CREATE OR REPLACE PROCEDURE proj_atualiza_inicio_cron ( nr_sequencia_p bigint) AS $body$
DECLARE


nr_seq_superior_w		bigint;
dt_inicio_real_w		timestamp;

BEGIN

begin
select	nr_seq_superior,
	dt_inicio_real
into STRICT	nr_seq_superior_w,
	dt_inicio_real_w
from	proj_cron_etapa
where	nr_sequencia	= nr_sequencia_p;
exception
	when others then
	dt_inicio_real_w := null;
end;

while(nr_seq_superior_w IS NOT NULL AND nr_seq_superior_w::text <> '') loop
	begin
	if (dt_inicio_real_w IS NOT NULL AND dt_inicio_real_w::text <> '') then
		update	proj_cron_etapa
		set	dt_inicio_real	= dt_inicio_real_w
		where	nr_sequencia	= nr_seq_superior_w
		and	coalesce(dt_inicio_real::text, '') = '';
	end if;

	select	max(nr_seq_superior)
	into STRICT	nr_seq_superior_w
	from	proj_cron_etapa
	where	nr_sequencia	= nr_seq_superior_w;
	end;

end loop;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
