CREATE OR REPLACE PROCEDURE proj_atual_hr_etapa_superior ( nr_sequencia_p bigint) AS $body$
DECLARE


nr_seq_superior_w	bigint;
hr_adic_w		double precision;


BEGIN

select	max(a.nr_seq_superior)
into STRICT	nr_seq_superior_w
from	proj_cron_etapa a
where	a.nr_sequencia		= nr_sequencia_p;

select	sum(qt_horas_etapa_adic)
into STRICT	hr_adic_w
from	proj_cron_etapa
where	nr_seq_superior		= nr_seq_superior_w;

update	proj_cron_etapa
set	qt_horas_etapa_adic	= hr_adic_w
where	nr_sequencia		= nr_seq_superior_w;

if (coalesce(nr_seq_superior_w, 0) <> 0) then
	CALL proj_atual_hr_etapa_superior(nr_seq_superior_w);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
