SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE proj_cancelar_projeto (nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_itens_w	bigint;


BEGIN
select	count(*)
into STRICT	qt_itens_w
from	com_cliente_hist
where 	nr_seq_projeto = nr_sequencia_p
and	nr_seq_tipo = 21;

if (qt_itens_w = 0) then
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(262017); -- Para cancelamento do projeto é necessário histórico do tipo cancelamento
end if;

update	proj_projeto
set	dt_cancelamento = clock_timestamp(),
	nm_usuario_cancelamento = nr_sequencia,
	nr_seq_estagio = 26,
	ie_status = 'F'
where nr_sequencia = nr_sequencia_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
