SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE proj_concluir_oficializacao ( nr_seq_oficializacao_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE

/*ie_opcao_p
	D - Desfazer conclusão da oficialização
	C - Fazer conclusão da oficialização
*/
nr_seq_etapa_w		bigint;
NR_SEQ_CLIENTE_w	bigint;

C01 CURSOR FOR
	SELECT	nr_seq_etapa
	from	com_cli_ofic_uso_mod
	where	nr_seq_ofic	= nr_seq_oficializacao_p;


BEGIN

if (ie_opcao_p = 'C') then
	update	com_cli_ofic_uso
	set	dt_oficializacao = clock_timestamp(),
		nm_usuario = nm_usuario_p
	where	nr_sequencia	= nr_seq_oficializacao_p;

	select	max(nr_seq_cliente)
	into STRICT	NR_SEQ_CLIENTE_w
	from	com_cli_ofic_uso
	where	nr_sequencia	= nr_seq_oficializacao_p;

	open C01;
	loop
	fetch C01 into
		nr_seq_etapa_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		update	proj_oficializacao_uso a
		set	a.dt_oficializacao	= clock_timestamp()
		where	a.nr_seq_etapa	= nr_seq_etapa_w
		and	a.nr_seq_proj in (SELECT x.nr_sequencia
			from	proj_projeto x
			where	x.nr_seq_cliente = NR_SEQ_CLIENTE_w);

		end;
	end loop;
	close C01;

elsif (ie_opcao_p = 'D') then
	update	com_cli_ofic_uso
	set	dt_oficializacao  = NULL,
		nm_usuario = nm_usuario_p
	where	nr_sequencia	= nr_seq_oficializacao_p;

	open C01;
	loop
	fetch C01 into
		nr_seq_etapa_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin

		update	proj_oficializacao_uso a
		set	a.dt_oficializacao	 = NULL
		where	a.nr_seq_etapa	= nr_seq_etapa_w
		and	a.nr_seq_proj in (SELECT x.nr_sequencia
			from	proj_projeto x
			where	x.nr_seq_cliente = NR_SEQ_CLIENTE_w);

		end;
	end loop;
	close C01;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE proj_concluir_oficializacao ( nr_seq_oficializacao_p bigint, ie_opcao_p text, nm_usuario_p text) FROM PUBLIC;

