SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE proj_consistir_calendario (dt_inicio_p timestamp, dt_fim_p timestamp, nr_seq_etapa_cron_p bigint, ds_erro_p INOUT text ) AS $body$
DECLARE


nr_seq_cronograma_w	bigint;
ie_dia_Semana_w		integer;
ds_retorno_w		varchar(4000);

BEGIN
ds_erro_p := '';

if (coalesce(nr_seq_etapa_cron_p,0) > 0) and (dt_fim_p IS NOT NULL AND dt_fim_p::text <> '') and (dt_inicio_p IS NOT NULL AND dt_inicio_p::text <> '') then

	select	max(nr_seq_cronograma)
	into STRICT	nr_seq_cronograma_w
	from 	proj_cron_etapa
	where	nr_sequencia = nr_seq_etapa_cron_p;

	if (coalesce(nr_seq_cronograma_w,0) > 0) then

		select  CASE WHEN count(*)=0 THEN 'S'  ELSE 'N' END
		into STRICT	ds_retorno_w
		from	proj_cron_calendario
		where	nr_seq_cronograma = nr_seq_cronograma_w
		and	hr_inicio <= TO_CHAR(dt_inicio_p,'hh24mi')
		and	hr_final >= TO_CHAR(dt_fim_p,'hh24mi');

		if (ds_retorno_w = 'S')	then
			ds_erro_p	:= WHEB_MENSAGEM_PCK.get_texto(277816,null);
		end if;

	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
