CREATE OR REPLACE PROCEDURE proj_copiar_equipe_cliente (nr_seq_equipe_p bigint, ds_lista_projeto_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_projeto_w	bigint;
nr_seq_nova_equipe_w	bigint;

c01 CURSOR FOR
SELECT	a.nr_sequencia
from	proj_projeto a
where	' ' || ds_lista_projeto_p || ' ' like '% ' || nr_sequencia || ' %'
and	(ds_lista_projeto_p IS NOT NULL AND ds_lista_projeto_p::text <> '');


BEGIN

open c01;
loop
fetch c01 into
	nr_seq_projeto_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	select	nextval('proj_equipe_seq')
	into STRICT	nr_seq_nova_equipe_w
	;

	insert into proj_equipe(nr_sequencia,
		nm_usuario,
		dt_atualizacao,
		nm_usuario_nrec,
		dt_atualizacao_nrec,
		nr_seq_cliente,
		dt_inicio,
		ds_objetivo,
		nr_seq_equipe_funcao,
		ie_interna_externa,
		ie_virada,
		nr_seq_apres,
		nr_seq_proj,
		dt_liberacao,
		nr_seq_virada)
	SELECT	nr_seq_nova_equipe_w,
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nr_seq_cliente,
		dt_inicio,
		ds_objetivo,
		nr_seq_equipe_funcao,
		ie_interna_externa,
		ie_virada,
		nr_seq_apres,
		nr_seq_projeto_w,
		dt_liberacao,
		nr_seq_virada
	from	proj_equipe a
	where	a.nr_sequencia	= nr_seq_equipe_p;

	insert into proj_equipe_papel(nr_sequencia,
		nr_seq_equipe,
		nm_usuario,
		dt_atualizacao,
		nm_usuario_nrec,
		dt_atualizacao_nrec,
		nr_seq_apres,
		nr_seq_funcao,
		nm_pessoa,
		cd_pessoa_fisica,
		ds_setor,
		ds_fone,
		dt_fim,
		nr_seq_cadastro,
		ds_atuacao,
		ds_cargo,
		nm_guerra,
		ds_email,
		ie_alerta,
		IE_ALOCADO_ORIGINAL)
	SELECT	nextval('proj_equipe_papel_seq'),
		nr_seq_nova_equipe_w,
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nr_seq_apres,
		nr_seq_funcao,
		nm_pessoa,
		cd_pessoa_fisica,
		ds_setor,
		ds_fone,
		dt_fim,
		nr_seq_cadastro,
		ds_atuacao,
		ds_cargo,
		nm_guerra,
		ds_email,
		ie_alerta,
		'S'
	from	proj_equipe_papel a
	where	a.nr_seq_equipe	= nr_seq_equipe_p;

	end;
end loop;
close c01;

commit;

end;
