CREATE OR REPLACE PROCEDURE proj_copiar_recursos_etapa (nr_seq_origem_p bigint, nr_seq_destino_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_equipe_w		bigint;
nr_seq_equipe_papel_w	bigint;

C01 CURSOR FOR
	SELECT	nr_seq_equipe,
		nr_seq_equipe_papel
	from	proj_cron_etapa_equipe a
	where	nr_seq_etapa_cron = nr_seq_origem_p;


BEGIN

delete	from proj_cron_etapa_equipe
where	nr_seq_etapa_cron = nr_seq_destino_p;
commit;

open C01;
loop
fetch C01 into
	nr_seq_equipe_w,
	nr_seq_equipe_papel_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	insert into proj_cron_etapa_equipe(nr_sequencia,
				nr_seq_etapa_cron,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nr_seq_equipe,
				nr_seq_equipe_papel)
			values (nextval('proj_cron_etapa_equipe_seq'),
				nr_seq_destino_p,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_equipe_w,
				nr_seq_equipe_papel_w);
	commit;

	end;
end loop;
close C01;

end;
$body$
