CREATE OR REPLACE PROCEDURE proj_copiar_requisitos ( nr_seq_requisitos_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, nr_seq_projeto_p bigint) AS $body$
DECLARE


nr_seq_requisitos_w		bigint;


BEGIN

if (nr_seq_projeto_p > 0) and (nr_seq_requisitos_p > 0) then
	select	nextval('des_requisito_seq')
	into STRICT	nr_seq_requisitos_w
	;

	insert into des_requisito(nr_sequencia, nr_seq_projeto, ds_titulo,
		 cd_analista, dt_requisito, dt_atualizacao,
		 nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
		 ie_cliente_wheb, nr_seq_apresentacao, nr_seq_ordem_serv,
		 ie_relatorio)
	(SELECT	 nr_seq_requisitos_w, nr_seq_projeto_p, ds_titulo,
		 cd_analista, clock_timestamp(), clock_timestamp(),
		 nm_usuario_p, clock_timestamp(), nm_usuario_p,
		 ie_cliente_wheb, nr_seq_apresentacao, nr_seq_ordem_serv,
		 'S'
	from	des_requisito
	where	nr_sequencia	= nr_seq_requisitos_p);

	insert into des_requisito_item(nr_sequencia, nr_seq_requisito, dt_atualizacao,
		 nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
		 nr_seq_apresentacao, ds_item, ds_titulo,
		 ie_status, ie_tipo_requisito, pr_realizacao)
	(SELECT	nextval('des_requisito_item_seq'), nr_seq_requisitos_w, clock_timestamp(),
		nm_usuario_p, clock_timestamp(), nm_usuario_p,
		nr_seq_apresentacao, ds_item, ds_titulo,
		'N', ie_tipo_requisito, pr_realizacao
	from	des_requisito_item
	where	nr_seq_requisito	= nr_seq_requisitos_p);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
