CREATE OR REPLACE PROCEDURE proj_desenv_agregar_func_cron ( nr_seq_cronograma_p bigint, ds_funcionalidade_p text, ie_funcionalidade_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_fase_implment_w	bigint;
nr_seq_cron_etapa_w	bigint;
nr_seq_apres_w		bigint;


BEGIN
if (nr_seq_cronograma_p IS NOT NULL AND nr_seq_cronograma_p::text <> '') and (ds_funcionalidade_p IS NOT NULL AND ds_funcionalidade_p::text <> '') and (ie_funcionalidade_p IS NOT NULL AND ie_funcionalidade_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	select	coalesce(max(nr_sequencia),0)
	into STRICT	nr_seq_fase_implment_w
	from	proj_cron_etapa
	where	nr_seq_cronograma = nr_seq_cronograma_p
	and	coalesce(ie_etapa_exec_desenv,'N') = 'S';

	if (nr_seq_fase_implment_w > 0) then
		begin
		select	nextval('proj_cron_etapa_seq')
		into STRICT	nr_seq_cron_etapa_w
		;

		select	coalesce(max(nr_seq_apres),0) + 10
		into STRICT	nr_seq_apres_w
		from	proj_cron_etapa
		where	nr_seq_cronograma = nr_seq_cronograma_p
		and	nr_seq_superior = nr_seq_fase_implment_w;

		insert into proj_cron_etapa(
			nr_sequencia,
			nr_seq_cronograma,
			dt_atualizacao,
			nm_usuario,
			ds_atividade,
			ie_fase,
			ds_etapa,
			dt_inicio_prev,
			dt_fim_prev,
			dt_inicio_real,
			dt_fim_real,
			qt_hora_prev,
			qt_hora_real,
			qt_hora_saldo,
			pr_etapa,
			nr_seq_apres,
			nr_seq_superior,
			ie_modulo,
			ie_etapa_exec_desenv)
		values (
			nr_seq_cron_etapa_w,
			nr_seq_cronograma_p,
			clock_timestamp(),
			nm_usuario_p,
			substr(ds_funcionalidade_p,1,255),
			'N',
			null,
			null,
			null,
			null,
			null,
			0,
			0,
			0,
			0,
			nr_seq_apres_w,
			nr_seq_fase_implment_w,
			'N',
			'N');
		end;
	end if;
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
