CREATE OR REPLACE PROCEDURE proj_desvincular_ordem_risco ( nr_seq_risco_p bigint, ds_lista_ordem_p text, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w	bigint;

C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	man_ordem_servico a
	where	' ' || ds_lista_ordem_p || ' ' like '% ' || a.nr_sequencia || ' %';


BEGIN

open c01;
loop
fetch c01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	update	man_ordem_servico
	set	nr_seq_risco_impl  = NULL,
		dt_atualizacao    = clock_timestamp(),
		nm_usuario        = nm_usuario_p
	where   nr_sequencia	  = nr_sequencia_w;
	end;
end loop;
close c01;

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
