CREATE OR REPLACE PROCEDURE proj_duplicar_agenda_cancelada ( nm_usuario_p text, nr_seq_agenda_p bigint, cd_estabelecimento_p bigint) AS $body$
DECLARE


cd_consultor_w		varchar(10);
dt_agenda_w		timestamp;
ie_dia_todo_w		varchar(1);
cd_hora_inic_w		timestamp;
cd_hora_fim_w		timestamp;
nr_seq_cliente_w		bigint;
nr_seq_canal_w		bigint;
nr_seq_motivo_w		bigint;
nr_seq_proj_w		bigint;
ds_observacao_w		varchar(2000);
ie_agenda_prevista_w	varchar(1);
ds_nome_curto_cliente_w	varchar(255);
ie_tipo_duplic_w	varchar(1);


BEGIN

ie_tipo_duplic_w := Obter_Param_Usuario(993, 138, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_tipo_duplic_w);

select	cd_consultor,
	dt_agenda,
	ie_dia_todo,
	cd_hora_inic,
	cd_hora_fim,
	nr_seq_cliente,
	nr_seq_canal,
	nr_seq_motivo,
	nr_seq_proj,
	ds_observacao,
	ie_agenda_prevista,
	ds_nome_curto_cliente
into STRICT	cd_consultor_w,
	dt_agenda_w,
	ie_dia_todo_w,
	cd_hora_inic_w,
	cd_hora_fim_w,
	nr_seq_cliente_w,
	nr_seq_canal_w,
	nr_seq_motivo_w,
	nr_seq_proj_w,
	ds_observacao_w,
	ie_agenda_prevista_w,
	ds_nome_curto_cliente_w
from	proj_agenda a
where	a.nr_sequencia = nr_seq_agenda_p;

if (ie_tipo_duplic_w = 'S') then

	insert into proj_agenda(	nr_sequencia,
				cd_consultor,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				dt_agenda,
				ie_status,
				ie_dia_todo,
				cd_hora_inic,
				cd_hora_fim,
				nr_seq_cliente,
				nr_seq_canal,
				nr_seq_motivo,
				nr_seq_proj,
				ds_observacao,
				ie_agenda_prevista,
				ds_nome_curto_cliente)
			values (	nextval('proj_agenda_seq'),
				cd_consultor_w,
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				dt_agenda_w,
				'Livre',
				ie_dia_todo_w,
				cd_hora_inic_w,
				cd_hora_fim_w,
				nr_seq_cliente_w,
				nr_seq_canal_w,
				nr_seq_motivo_w,
				nr_seq_proj_w,
				ds_observacao_w,
				ie_agenda_prevista_w,
				ds_nome_curto_cliente_w);

elsif (ie_tipo_duplic_w = 'D') then

	insert into proj_agenda(	nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				dt_agenda,
				ie_status,
				ie_dia_todo)
			values (	nextval('proj_agenda_seq'),
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				dt_agenda_w,
				'Livre',
				ie_dia_todo_w);

end if;

commit;

end;
$body$
