SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE proj_duplicar_posicionamento ( dt_ref_origem_p timestamp, nm_usuario_p text, dt_ref_destino_p timestamp, nr_seq_proj_p bigint) AS $body$
DECLARE


nr_sequencia_w		bigint;
ds_posicao_w		varchar(32000);
nr_seq_posic_item_w	bigint;
nr_seq_proj_w		bigint;
nr_seq_item_w		bigint;
ie_status_w		varchar(15);
nr_seq_tipo_posic_w	bigint;


C01 CURSOR FOR
	SELECT	nr_sequencia
	from	proj_posicao_coordenacao a
	where	trunc(dt_posicao) = trunc(dt_ref_origem_p)
	and	a.nr_seq_proj = nr_seq_proj_p
	and	exists (SELECT	1
			from	proj_tipo_posic_item c,
				proj_tipo_posicionamento d,
				proj_projeto b
			where	d.nr_seq_classif = b.nr_seq_classif
			and		c.nr_seq_tipo_posic = d.nr_sequencia
			and		coalesce(c.ie_status,b.ie_status) = b.ie_status
			and		b.nr_sequencia = a.nr_seq_proj
			and		a.nr_seq_posic_item = c.nr_sequencia)
	order by 1;

C02 CURSOR FOR
	SELECT	nr_sequencia
	from	proj_tipo_posic_item a
	where	a.nr_seq_tipo_posic = nr_seq_tipo_posic_w
	and	coalesce(ie_status,ie_status_w) = ie_status_w
	and 	not exists (SELECT	1
			    from 	proj_posicao_coordenacao b
			    where 	a.nr_sequencia = b.nr_seq_posic_item
			    and 	trunc(dt_posicao) = trunc(dt_ref_origem_p));


BEGIN
open C01;
loop
fetch C01 into
	nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	ds_posicao,
		nr_seq_posic_item,
		nr_seq_proj
	into STRICT	ds_posicao_w,
		nr_seq_posic_item_w,
		nr_seq_proj_w
	from	proj_posicao_coordenacao a
	where	nr_sequencia = nr_sequencia_w;

	insert into proj_posicao_coordenacao(nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				dt_posicao,
				dt_liberacao,
				ds_posicao,
				nr_seq_posic_item,
				nr_seq_proj,
				ie_importante,
				ie_ignorar)
			values (nextval('proj_posicao_coordenacao_seq'),
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				dt_ref_destino_p,
				null,
				ds_posicao_w,
				nr_seq_posic_item_w,
				nr_seq_proj_w,
				'N',
				'N');

	commit;

	end;
end loop;
close C01;

select	max(a.nr_sequencia),
	max(b.ie_status)
into STRICT	nr_seq_tipo_posic_w,
	ie_status_w
from	proj_tipo_posicionamento a,
	proj_projeto b
where	a.nr_seq_classif = b.nr_seq_classif
and	b.nr_sequencia = nr_seq_proj_p;

open C02;
loop
fetch C02 into
	nr_seq_item_w;
EXIT WHEN NOT FOUND; /* apply on C02 */
	begin
	insert into proj_posicao_coordenacao(nr_sequencia,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					dt_posicao,
					dt_liberacao,
					ds_posicao,
					nr_seq_posic_item,
					nr_seq_proj)
				values (nextval('proj_posicao_coordenacao_seq'),
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					dt_ref_destino_p,
					null,
					' ',
					nr_seq_item_w,
					nr_seq_proj_p);
	end;
end loop;
close C02;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
