CREATE OR REPLACE PROCEDURE proj_duplicar_regra_escore ( nr_seq_escore_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_escore_w			bigint;
nr_seq_escore_area_w		bigint;
nr_seq_escore_area_ww		bigint;
nr_seq_escore_item_w		bigint;
nr_seq_escore_item_ww		bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	proj_area_escore
	where	nr_seq_regra	= nr_seq_escore_p
	order by	nr_sequencia;

C02 CURSOR FOR
	SELECT	nr_sequencia
	from	proj_item_escore
	where	nr_seq_area = nr_seq_escore_area_w
	order by	nr_sequencia;


BEGIN

select	nextval('proj_regra_escore_seq')
into STRICT	nr_seq_escore_w
;

insert into proj_regra_escore(
	nr_sequencia,
 	dt_atualizacao,
	nm_usuario,
 	dt_atualizacao_nrec,
 	nm_usuario_nrec,
 	ds_regra,
 	dt_inicio_validade,
 	dt_fim_validade,
 	ie_tipo_setor )
SELECT	nr_seq_escore_w,
 	clock_timestamp(),
	nm_usuario_p,
 	clock_timestamp(),
 	nm_usuario_p,
 	ds_regra,
 	dt_inicio_validade,
 	dt_fim_validade,
 	ie_tipo_setor
from	proj_regra_escore
where	nr_sequencia	= nr_seq_escore_p;

open C01;
loop
fetch C01 into
	nr_seq_escore_area_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	nextval('proj_area_escore_seq')
	into STRICT	nr_seq_escore_area_ww
	;

	insert into proj_area_escore(
		ds_area,
		dt_atualizacao,
		dt_atualizacao_nrec,
		ie_situacao,
		nm_usuario,
		nm_usuario_nrec,
		nr_seq_apres,
		nr_seq_regra,
		nr_sequencia,
		qt_escore)
	SELECT	ds_area,
		clock_timestamp(),
		clock_timestamp(),
		ie_situacao,
		nm_usuario_p,
		nm_usuario_p,
		nr_seq_apres,
		nr_seq_escore_w,
		nr_seq_escore_area_ww,
		qt_escore
	from	proj_area_escore
	where	nr_sequencia	= nr_seq_escore_area_w;

	open C02;
	loop
	fetch C02 into
		nr_seq_escore_item_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin

		insert into proj_item_escore(
			ds_item,
			dt_atualizacao,
			dt_atualizacao_nrec,
			ie_regra_definicao,
			ie_situacao,
			nm_usuario,
			nm_usuario_nrec,
			nr_seq_apres,
			nr_seq_area,
			nr_seq_modelo_avaliacao,
			nr_sequencia,
			qt_escore)
		SELECT	ds_item,
			clock_timestamp(),
			clock_timestamp(),
			ie_regra_definicao,
			ie_situacao,
			nm_usuario_p,
			nm_usuario_p,
			nr_seq_apres,
			nr_seq_escore_area_ww,
			nr_seq_modelo_avaliacao,
			nextval('proj_item_escore_seq'),
			qt_escore
		from	proj_item_escore
		where	nr_sequencia	= nr_seq_escore_item_w;

		end;
	end loop;
	close C02;

	end;
end loop;
close C01;

commit;

end;
$body$
