open c01;
loop
fetch c01 into
nr_sequencia_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	CALL proj_excluir_etapa_cron(nr_seq_cronograma_p, nr_sequencia_w, nm_usuario_p);
end loop;
close c01;

