CREATE OR REPLACE PROCEDURE proj_excluir_etapa_cron ( nr_seq_cronograma_p bigint, nr_seq_etapa_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_atividade_w	bigint;

c01 CURSOR FOR
SELECT	nr_sequencia
from	proj_cron_etapa
where	nr_seq_cronograma = nr_seq_cronograma_p
and	nr_seq_superior = nr_seq_etapa_p
order by
	cd_classificacao;


BEGIN
if (nr_seq_cronograma_p IS NOT NULL AND nr_seq_cronograma_p::text <> '') and (nr_seq_etapa_p IS NOT NULL AND nr_seq_etapa_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	open c01;
	loop
	fetch c01 into nr_seq_atividade_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		CALL proj_excluir_etapa_cron(nr_seq_cronograma_p, nr_seq_atividade_w, nm_usuario_p);

		delete
		from	proj_cron_etapa
		where	nr_sequencia = nr_seq_atividade_w;
		end;
	end loop;
	close c01;

	delete
	from	proj_cron_etapa
	where	nr_sequencia = nr_seq_etapa_p;

	CALL proj_recalcula_cronograma(nr_seq_cronograma_p);
	CALL atualizar_total_horas_cron(nr_seq_cronograma_p);
	CALL proj_desenv_atual_perc_cron(nr_seq_cronograma_p, nm_usuario_p);
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
