CREATE OR REPLACE PROCEDURE proj_faturar_rat ( nr_seq_cliente_p bigint, nr_seq_nf_p bigint, nm_usuario_p text, nr_seq_projeto_p bigint default -1, ds_rat_p INOUT text DEFAULT NULL, vl_cobrar_p INOUT bigint DEFAULT NULL) AS $body$
DECLARE


vl_cobrar_w		double precision;
nr_seq_rat_w		bigint;
ds_rat_w		varchar(10000) := '';
ds_virgula_w		varchar(10) := '';
dt_inicio_w		timestamp := to_date('01/05/2006','dd/mm/yyyy');

C01 CURSOR FOR
SELECT	a.nr_sequencia
from	proj_rat a
where	a.nr_seq_nf		= nr_seq_nf_p
and 	(dt_aprov_fin IS NOT NULL AND dt_aprov_fin::text <> '');


BEGIN
update	proj_rat
set	nr_seq_nf		= nr_seq_nf_p,
	ie_faturado		= 'S'
where	nr_seq_cliente		= nr_seq_cliente_p
and	(dt_aprovacao IS NOT NULL AND dt_aprovacao::text <> '')
and	dt_aprovacao 		>= dt_inicio_w
and 	(dt_aprov_fin IS NOT NULL AND dt_aprov_fin::text <> '')
and	coalesce(nr_seq_nf::text, '') = ''
and (nr_seq_projeto_p = -1 or nr_seq_proj =  nr_seq_projeto_p)
and	ie_status_cobrar = 'CV'
and	coalesce(vl_cobrar_cliente,0) > 0;

select	sum(a.vl_cobrar_cliente)
into STRICT	vl_cobrar_w
from	proj_rat a
where	a.nr_seq_nf		= nr_seq_nf_p;

open c01;
loop
fetch c01 into
	nr_seq_rat_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	ds_rat_w		:= ds_rat_w || ds_virgula_w || nr_seq_rat_w;
	ds_virgula_w		:= ', ';
	end;
end loop;
close c01;

ds_rat_p	:= substr(ds_rat_w,1,250);
vl_cobrar_p	:= coalesce(vl_cobrar_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
