SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE proj_gerar_avaliacao_quesito ( nr_seq_escore_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_escore_item_w		bigint;
nr_seq_item_escore_w		bigint;
nr_seq_modelo_avaliacao_w	bigint;
nr_seq_avaliacao_w		bigint;
qt_registro_w			integer	:= 0;

C01 CURSOR FOR /* Buscar os itens gerados para o escore */
	SELECT	b.nr_sequencia,
		b.nr_seq_item
	from	proj_escore_item	b,
		proj_escore		a
	where	a.nr_sequencia	= nr_seq_escore_p
	and	a.nr_sequencia	= b.nr_seq_escore;

C02 CURSOR FOR /* Buscar os quesitos do modelo de avaliação cadastro no item do escore gerado */
	SELECT	nr_sequencia
	from	proj_avaliacao
	where	nr_seq_modelo	= nr_seq_modelo_avaliacao_w;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_escore_item_w,
	nr_seq_item_escore_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	/* Buscar o modelo de avaliação no cadastro do item */

	select	coalesce(nr_seq_modelo_avaliacao,0)
	into STRICT	nr_seq_modelo_avaliacao_w
	from	proj_item_escore
	where	nr_sequencia	= nr_seq_item_escore_w;

	open C02;
	loop
	fetch C02 into
		nr_seq_avaliacao_w;
	EXIT WHEN NOT FOUND; /* apply on C02 */
		begin
		select	count(*) /* Verificar se o quesito já está lançado no item */
		into STRICT	qt_registro_w
		from	proj_avaliacao_quesito
		where	nr_seq_escore_item	= nr_seq_escore_item_w
		and	nr_seq_avaliacao	= nr_seq_avaliacao_w;

		if (qt_registro_w	= 0) then
			insert into proj_avaliacao_quesito(nr_sequencia, nr_seq_avaliacao, dt_atualizacao,
				nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
				nr_seq_escore_item)
			values (	nextval('proj_avaliacao_quesito_seq'), nr_seq_avaliacao_w, clock_timestamp(),
				nm_usuario_p, clock_timestamp(), nm_usuario_p,
				nr_seq_escore_item_w);
		end if;
		end;
	end loop;
	close C02;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
