CREATE OR REPLACE PROCEDURE proj_gerar_comunicacao_pmo (nr_seq_comunicacao_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_projeto_w			bigint;
nr_sequencia_w				bigint;


c01 CURSOR FOR
SELECT 	a.nr_sequencia
from 	proj_projeto a
where 	1 = 1
and 	ie_status = 'E'
and 	ie_origem = 'D'
and		nr_seq_classif not in (22,45)
and 	coalesce(ie_nivel_projeto,0) <> '3'
and	nr_seq_estagio <> 15
and	not exists (select 1
	from	proj_comunicacao_projeto x
	where	x.nr_seq_projeto	= a.nr_sequencia
	and	x.nr_seq_comunicacao	= nr_seq_comunicacao_p)
order by 	a.nr_seq_prioridade;


BEGIN

open c01;
loop
fetch c01 into
	nr_seq_projeto_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	if (nr_seq_projeto_w IS NOT NULL AND nr_seq_projeto_w::text <> '') then
		select	nextval('proj_comunicacao_projeto_seq')
		into STRICT	nr_sequencia_w
		;

		insert	into PROJ_COMUNICACAO_PROJETO(NR_SEQUENCIA,
			NR_SEQ_COMUNICACAO,
			NR_SEQ_PROJETO,
			DT_ATUALIZACAO,
			NM_USUARIO,
			DT_ATUALIZACAO_NREC,
			NM_USUARIO_NREC,
			IE_NECESSITA_COMUNICACAO,
			IE_COMUNICADO,
			DS_OBSERVACAO_PMO)
		values (nr_sequencia_w,
			nr_seq_comunicacao_p,
			nr_seq_projeto_w,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			'S',
			'N',
			null);

	end if;
	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
