CREATE OR REPLACE PROCEDURE proj_gerar_cons_aval_quesito (nr_seq_avaliacao_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;
ie_tipo_avaliacao_w	varchar(3);
nr_seq_modelo_w		bigint;

c01 CURSOR FOR
	SELECT	a.nr_sequencia,
		a.ie_tipo_avaliacao
	from	PROJ_AVALIACAO a
	where	a.ie_situacao	= 'A'
	and		nr_seq_modelo	= nr_seq_modelo_w
	order by nr_seq_apres;

BEGIN

select	nr_seq_modelo
into STRICT	nr_seq_modelo_w
from	proj_consultor_aval
where	nr_sequencia = nr_seq_avaliacao_p;

delete from PROJ_CONSULTOR_AVAL_QUESITO
where	NR_SEQ_AVALIACAO = nr_seq_avaliacao_p;

open c01;
loop
fetch c01 into
	nr_sequencia_w,
	ie_tipo_avaliacao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */

	insert into PROJ_CONSULTOR_AVAL_QUESITO(NR_SEQUENCIA,
		NR_SEQ_AVALIACAO,
		DT_ATUALIZACAO,
		NM_USUARIO,
		IE_TIPO_AVALIACAO,
		NR_SEQ_QUESITO,
		IE_RESULT_CB,
		NR_SEQ_OPCAO,
		QT_PONTO)
	values (nextval('proj_consultor_aval_quesito_se'),
		nr_seq_avaliacao_p,
		clock_timestamp(),
		nm_usuario_p,
		ie_tipo_avaliacao_w,
		nr_sequencia_w,
		null,
		null,
		null);

end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
