SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE proj_gerar_cronograma_req ( nr_seq_requisito_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_cronograma_w 	bigint;
nr_seq_superior_w	bigint;
nr_seq_apresent_w	bigint;
nr_seq_projeto_w	bigint;
ds_requisito_w		varchar(255);
ds_item_w		varchar(4000);

c01 CURSOR FOR
SELECT	a.ds_titulo,
	a.nr_seq_apresentacao,
	a.ds_item
from	des_requisito_item a,
	des_requisito b
where	a.nr_seq_requisito	= b.nr_sequencia
and	b.nr_sequencia		= nr_seq_requisito_p
and	not exists (select	1
			from	proj_cron_etapa a
			where	a.nr_seq_cronograma	= nr_seq_cronograma_w
			and	a.nr_seq_superior	= nr_seq_superior_w
			and	a.nr_seq_apres		= a.nr_seq_apresentacao);


BEGIN
select	max(a.nr_seq_projeto)
into STRICT	nr_seq_projeto_w
from	des_requisito a
where	a.nr_sequencia	= nr_seq_requisito_p;

select	max(a.nr_sequencia)
into STRICT	nr_seq_cronograma_w
from	proj_cronograma a
where	a.nr_seq_proj	= nr_seq_projeto_w
and	a.ie_tipo_cronograma	= 'E';

if (nr_seq_cronograma_w IS NOT NULL AND nr_seq_cronograma_w::text <> '') then
	/* Procurar o cronograma de execução - Implementação , esse será o superior */

	select	max(a.nr_sequencia)
	into STRICT	nr_seq_superior_w
	from	proj_cron_etapa a
	where	a.nr_seq_cronograma	= nr_seq_cronograma_w
	and	upper(a.ds_atividade)		like '%IMPLEMENTAÇÃO%'
	and	a.ie_fase		= 'S';

	if (nr_seq_superior_w IS NOT NULL AND nr_seq_superior_w::text <> '') then
		open c01;
		loop
		fetch c01 into
			ds_requisito_w,
			nr_seq_apresent_w,
			ds_item_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			begin
			insert into proj_cron_etapa(
				nr_sequencia,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				dt_atualizacao,
				nm_usuario,
				qt_hora_prev,
				pr_etapa,
				ie_modulo,
				ie_fase,
				nr_seq_apres,
				nr_seq_cronograma,
				ds_atividade,
				nr_seq_superior,
				ds_etapa)
			values (
				nextval('proj_cron_etapa_seq'),
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				0,
				0,
				'N',
				'N',
				nr_seq_apresent_w,
				nr_seq_cronograma_w,
				ds_requisito_w,
				nr_seq_superior_w,
				ds_item_w);
			end;
		end loop;
		close c01;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
