CREATE OR REPLACE PROCEDURE proj_gerar_cronograma (nr_seq_projeto_p bigint, ie_tipo_cronograma_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_cronograma_w	bigint;
qt_registros_w		smallint;


BEGIN
if (nr_seq_projeto_p IS NOT NULL AND nr_seq_projeto_p::text <> '') then
	if (obter_se_contido_char('I', ie_tipo_cronograma_p )  = 'S') then
		--verifica se já existe este tipo criado
		select	count(nr_sequencia)
		into STRICT	qt_registros_w
		from 	proj_cronograma
		where	nr_seq_proj = nr_seq_projeto_p
		and	ie_tipo_cronograma = 'I'  LIMIT 1;

		if (qt_registros_w < 1) then
			nr_seq_cronograma_w := proj_desenv_gerar_cron_inic(nr_seq_projeto_p, nm_usuario_p, nr_seq_cronograma_w, 'D');
		end if;
	end if;

	if (obter_se_contido_char('P', ie_tipo_cronograma_p ) = 'S') then
		--verifica se já existe este tipo criado
		select	count(nr_sequencia)
		into STRICT	qt_registros_w
		from 	proj_cronograma
		where	nr_seq_proj = nr_seq_projeto_p
		and	ie_tipo_cronograma = 'P'  LIMIT 1;

		if (qt_registros_w < 1) then
			nr_seq_cronograma_w := proj_desenv_gerar_cron_planej(nr_seq_projeto_p, nm_usuario_p, nr_seq_cronograma_w, 'D');
		end if;
	end if;

	if (obter_se_contido_char('E', ie_tipo_cronograma_p ) = 'S') then
		--verifica se já existe este tipo criado
		select	count(nr_sequencia)
		into STRICT	qt_registros_w
		from 	proj_cronograma
		where	nr_seq_proj = nr_seq_projeto_p
		and	ie_tipo_cronograma = 'E'  LIMIT 1;

		if (qt_registros_w < 1) then
			nr_seq_cronograma_w := proj_desenv_gerar_cron_exec(nr_seq_projeto_p, nm_usuario_p, nr_seq_cronograma_w, 'D');
		end if;
	end if;
end if;

commit;

end;
$body$
