SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE proj_gerar_escore_os_atrasada ( nr_seq_item_p bigint, nr_seq_proj_p bigint, qt_escore_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_ponto_w			double precision;
qt_peso_w			double precision;
qt_escore_real_w		double precision;
pr_escore_real_w		double precision;
nr_seq_cliente_w		double precision;
dt_inicio_real_w		timestamp;
dt_fim_real_w			timestamp;
cd_cnpj_w			varchar(14);
nr_seq_loc_w			bigint;


BEGIN

select	nr_seq_cliente,
	coalesce(dt_inicio_real, dt_inicio_prev),
	coalesce(dt_fim_real, clock_timestamp())
into STRICT	nr_seq_cliente_w,
	dt_inicio_real_w,
	dt_fim_real_w
from	proj_projeto
where	nr_sequencia		= nr_seq_proj_p;

select	cd_cnpj
into STRICT	cd_cnpj_w
from	com_cliente
where	nr_sequencia		= nr_seq_cliente_w;
select	min(nr_sequencia)
into STRICT	nr_seq_loc_w
from	man_localizacao
where	cd_cnpj			= cd_cnpj_w;

select	count(*)
into STRICT	qt_peso_w
from	man_ordem_servico a
where	a.nr_seq_localizacao	= nr_seq_loc_w
and	a.dt_ordem_servico between dt_inicio_real_w and dt_fim_real_w;

select	count(*)
into STRICT	qt_ponto_w
from	man_ordem_servico a
where	a.nr_seq_localizacao	= nr_seq_loc_w
and	coalesce(dt_fim_real,clock_timestamp())	> a.dt_conclusao_desejada;


if (qt_peso_w >	0)  then
	qt_escore_real_w		:= trunc(qt_escore_p * ((qt_peso_w - qt_ponto_w) / qt_peso_w),2);
	if (qt_escore_real_w	< 0) then /* Felipe - 136013 - Solicitado pelo Daniel para o valor nÃ£o ser negativo*/
		qt_escore_real_w	:= 0;
	end if;
	pr_escore_real_w		:= trunc(dividir(qt_escore_real_w * 100, qt_escore_p),2);
else
	qt_escore_real_w	:= qt_escore_p;
	pr_escore_real_w	:= 100;
end if;

update	proj_escore_item
set	qt_nota_maxima	= trunc(qt_peso_w,2),
	qt_nota		= trunc(qt_ponto_w,2),
	qt_escore_real	= qt_escore_real_w,
	pr_escore_real	= pr_escore_real_w
where	nr_sequencia	= nr_seq_item_p;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
