CREATE OR REPLACE PROCEDURE proj_gerar_escore_os_entregue (nr_seq_projeto_p bigint, nm_usuario_p text, qt_escore_p bigint, nr_seq_item_p bigint) AS $body$
DECLARE


qt_os_entregue_w		bigint;
qt_total_os_w			bigint;
qt_escore_w			bigint;


BEGIN
	select 	count(*)
	into STRICT	qt_os_entregue_w
	from	man_ordem_servico a,
		proj_projeto b
	where	a.nr_sequencia = b.nr_seq_ordem_serv
	and	b.nr_sequencia = nr_seq_projeto_p
	and	exists (SELECT	1
			from	Man_ordem_serv_estagio c
			where	c.nr_seq_ordem = a.nr_Sequencia
			and	c.nr_seq_estagio = 2
			and	c.dt_atualizacao = (select	max(dt_atualizacao)
						   from		man_ordem_serv_estagio d
						   where	d.nr_sequencia = c.nr_sequencia
						   and		d.nr_seq_estagio = 2)
			and	c.dt_atualizacao <= coalesce(DT_INTERNA_ACORDO,DT_FIM_PREVISTO));

	select 	count(*)
	into STRICT	qt_total_os_w
	from	man_ordem_servico a,
		proj_projeto b
	where	a.nr_sequencia = b.nr_seq_ordem_serv
	and	b.nr_sequencia = nr_seq_projeto_p
	and	exists (SELECT	1
			from	Man_ordem_serv_estagio c
			where	c.nr_seq_ordem = a.nr_Sequencia
			--and	c.nr_seq_estagio = 2
			and	c.dt_atualizacao = (select	max(dt_atualizacao)
						   from		man_ordem_serv_estagio d
						   where	d.nr_sequencia = c.nr_sequencia
						   --and		d.nr_seq_estagio = 2)
						   )
			and	c.dt_atualizacao <= coalesce(DT_INTERNA_ACORDO,DT_FIM_PREVISTO));

	if (qt_total_os_w > 0) and (qt_os_entregue_w > 0) then
		qt_escore_w := ((qt_os_entregue_w * 100) / qt_total_os_w);
	else
		qt_escore_w := qt_escore_p;
	end if;

	update 	proj_escore_item
	set	qt_escore_real = qt_escore_w
	where	nr_sequencia = nr_seq_item_p;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
