SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE proj_gerar_etapas_projeto ( nr_seq_projeto_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_etapa_w			bigint;
nr_seq_etapa_cron_w		bigint;
qt_etapa_w			integer;
nr_seq_cliente_w		bigint;
dt_oficializacao_w		timestamp;
IE_MAPEAMENTO_PROCESSO_w        varchar(03);
IE_ANALISE_ADERENCIA_w          varchar(03);
IE_PARAMETRIZACAO_w             varchar(03);
IE_TREINAMENTO_w                varchar(03);
IE_PROVA_w                      varchar(03);
IE_TESTE_PILOTO_w               varchar(03);

C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	proj_cron_etapa a,
		proj_cronograma b
	where	a.nr_seq_cronograma	= b.nr_sequencia
	and	(a.nr_seq_etapa IS NOT NULL AND a.nr_seq_etapa::text <> '')
	and	b.nr_seq_proj	= nr_seq_projeto_p
	and	coalesce(a.ie_situacao,'A') = 'A';


BEGIN

select	max(nr_seq_cliente)
into STRICT	nr_seq_cliente_w
from 	proj_projeto
where	nr_sequencia	= nr_seq_projeto_p;

open C01;
loop
fetch C01 into
	nr_seq_etapa_cron_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	count(*)
	into STRICT	qt_etapa_w
	from	proj_oficializacao_uso
	where	nr_seq_etapa_cron = nr_seq_etapa_cron_w
	and	nr_seq_proj = nr_seq_projeto_p;

	if (qt_etapa_w = 0) then
		select	nr_seq_etapa
		into STRICT	nr_seq_etapa_w
		from	proj_cron_etapa
		where	nr_sequencia	= nr_seq_etapa_cron_w;

		select	max(a.DT_OFICIALIZACAO),
			max(a.IE_MAPEAMENTO_PROCESSO),
			max(a.IE_ANALISE_ADERENCIA),
			max(a.IE_PARAMETRIZACAO),
			max(a.IE_TREINAMENTO),
			max(a.IE_PROVA),
			max(a.IE_TESTE_PILOTO)
		into STRICT	dt_oficializacao_w,
			IE_MAPEAMENTO_PROCESSO_w,
			IE_ANALISE_ADERENCIA_w,
			IE_PARAMETRIZACAO_w,
			IE_TREINAMENTO_w,
			IE_PROVA_w,
			IE_TESTE_PILOTO_w
		from	proj_oficializacao_uso a,
			proj_projeto b
		where	a.nr_seq_proj		= b.nr_sequencia
		and	b.nr_seq_cliente	= nr_seq_cliente_w
		and	NR_SEQ_ETAPA		= nr_seq_etapa_w;


		insert into proj_oficializacao_uso(	nr_sequencia,
							nr_seq_proj,
							nr_seq_etapa,
							dt_atualizacao,
							nm_usuario,
							dt_atualizacao_nrec,
							nm_usuario_nrec,
							nr_seq_etapa_cron,
							dt_oficializacao,
							IE_MAPEAMENTO_PROCESSO,
							IE_ANALISE_ADERENCIA,
							IE_PARAMETRIZACAO,
							IE_TREINAMENTO,
							IE_PROVA,
							IE_TESTE_PILOTO)
						values (	nextval('proj_oficializacao_uso_seq'),
							nr_seq_projeto_p,
							nr_seq_etapa_w,
							clock_timestamp(),
							nm_usuario_p,
							clock_timestamp(),
							nm_usuario_p,
							nr_seq_etapa_cron_w,
							dt_oficializacao_w,
							IE_MAPEAMENTO_PROCESSO_w,
							IE_ANALISE_ADERENCIA_w,
							IE_PARAMETRIZACAO_w,
							IE_TREINAMENTO_w,
							IE_PROVA_w,
							IE_TESTE_PILOTO_w);
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE proj_gerar_etapas_projeto ( nr_seq_projeto_p bigint, nm_usuario_p text) FROM PUBLIC;

