CREATE OR REPLACE PROCEDURE proj_gerar_item_rot ( nr_sequencia_p bigint, nr_seq_roteiro_p bigint, nm_usuario_p text, ds_modulo_p text) AS $body$
DECLARE


tam_lista_w	bigint;
nr_seq_item_w	bigint;
nr_sequencia_w	bigint;
nr_seq_nao_sel_w	bigint;
ie_pos_virgula_w	smallint	:= 0;
ds_lista_modulo_w	varchar(4000);


BEGIN
ds_lista_modulo_w	:= ds_modulo_p;
if (ds_lista_modulo_w IS NOT NULL AND ds_lista_modulo_w::text <> '') then
	begin
	WHILE(ds_lista_modulo_w IS NOT NULL AND ds_lista_modulo_w::text <> '') LOOP
		begin
		tam_lista_w			:= length(ds_lista_modulo_w);
		ie_pos_virgula_w			:= position(',' in ds_lista_modulo_w);

		if (ie_pos_virgula_w <> 0) then
			nr_seq_nao_sel_w		:= (substr(ds_lista_modulo_w,1,(ie_pos_virgula_w - 1)))::numeric;
			ds_lista_modulo_w		:= substr(ds_lista_modulo_w, (ie_pos_virgula_w + 1), tam_lista_w);
		end if;

		select	nextval('proj_tp_cli_rot_item_seq')
		into STRICT	nr_sequencia_w
		;

		insert into proj_tp_cli_rot_item(
			nr_sequencia,
			nr_seq_rot_cli,
			dt_atualizacao,
			nm_usuario,
			nr_seq_item,
			ie_resultado)
		values (	nr_sequencia_w,
			nr_sequencia_p,
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_nao_sel_w,
			null);
		end;
	END loop;
	end;
commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
