SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE proj_gerar_itens_analise_rh ( nr_seq_analise_rh_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_prev_impl_w		bigint;
dt_prev_inic_impl_w		timestamp;
nr_seq_cliente_w		bigint;
nr_meses_analise_w		bigint;
nr_seq_nivel_prof_w		bigint;
qt_profissionais_w		bigint;
nr_horas_prof_mes_w		bigint;
dt_analise_inicial_w		timestamp;
dt_analise_final_w		timestamp;



C01 CURSOR FOR
	SELECT	a.nr_sequencia,
		PKG_DATE_UTILS.start_of(a.dt_prev_inic_impl,'MONTH', 0),
		nr_seq_cliente
	from	com_prev_implant a
	where	(a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
	and	coalesce(a.dt_cancelamento::text, '') = ''
	and	a.dt_prev_inic_impl between dt_analise_inicial_w and (dt_analise_final_w)
	and	exists (SELECT	1
			from	com_prev_implant_prof x
			where	x.nr_seq_prev_impl = a.nr_sequencia);


BEGIN

select	dt_analise_inicial,
	nr_meses_analise
into STRICT	dt_analise_inicial_w,
	nr_meses_analise_w
from	proj_analise_rh
where	nr_sequencia = nr_seq_analise_rh_p;

dt_analise_final_w := PKG_DATE_UTILS.ADD_MONTH(dt_analise_inicial_w,nr_meses_analise_w, 0);

open C01;
loop
fetch C01 into
	nr_seq_prev_impl_w,
	dt_prev_inic_impl_w,
	nr_seq_cliente_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	select	max(nr_seq_nivel_prof),
		sum(qt_profissionais),
		sum(nr_horas_prof_mes)
	into STRICT	nr_seq_nivel_prof_w,
		qt_profissionais_w,
		nr_horas_prof_mes_w
	from	com_prev_implant_prof
	where	nr_seq_prev_impl = nr_seq_prev_impl_w;
	--group by nr_seq_nivel_prof;      OS 397409 alterado pois trazia mais de uma linha.
	insert into proj_analise_rh_item(nr_sequencia,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					nr_seq_nivel_prof,
					qt_prof,
					hr_prof,
					nr_seq_analise_rh,
					dt_mes_referencia,
					nr_seq_cliente)
				values (nextval('proj_analise_rh_item_seq'),
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					nr_seq_nivel_prof_w,
					qt_profissionais_w,
					nr_horas_prof_mes_w,
					nr_seq_analise_rh_p,
					dt_prev_inic_impl_w,
					nr_seq_cliente_w);
	commit;

	end;
end loop;
close C01;



end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
