CREATE OR REPLACE PROCEDURE proj_gerar_itens_classif ( nr_seq_projeto_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_classif_w	bigint;
nr_seq_item_w		bigint;
ie_status_w		varchar(1);
ie_funcao_exec_w	varchar(15);

C01 CURSOR FOR
	SELECT	a.nr_sequencia,
		a.ie_status,
		ie_funcao_exec
	from	proj_item a,
		proj_classif_item b
	where	a.nr_sequencia = b.nr_seq_item
	and	b.nr_seq_classif = nr_seq_classif_w;


BEGIN

select	nr_seq_classif
into STRICT	nr_seq_classif_w
from	proj_projeto
where	nr_sequencia = nr_seq_projeto_p;

if (nr_seq_classif_w IS NOT NULL AND nr_seq_classif_w::text <> '') then
	open C01;
	loop
	fetch C01 into
		nr_seq_item_w,
		ie_status_w,
		ie_funcao_exec_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */

	insert into proj_projeto_item(nr_sequencia,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					nr_seq_projeto,
					ie_status,
					nr_seq_item,
					ie_realizado,
					ie_funcao_exec)
			values (nextval('proj_projeto_item_seq'),
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					nr_seq_projeto_p,
					ie_status_w,
					nr_seq_item_w,
					'N',
					ie_funcao_exec_w);
	commit;

	end loop;
	close C01;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
