SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE proj_gerar_itens_escore ( nr_seq_escore_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_regra_w			bigint;
nr_seq_area_w			bigint;
nr_seq_item_w			bigint;
qt_escore_w			bigint;
qt_registro_w			integer	:= 0;
nr_seq_modelo_avaliacao_w	bigint;
nr_seq_proj_escore_item_w	bigint;
nr_seq_avaliacao_w		bigint;

C01 CURSOR FOR
	SELECT	a.nr_sequencia,
		b.nr_sequencia,
		b.qt_escore,
		coalesce(b.nr_seq_modelo_avaliacao,0)
	from	proj_item_escore	b,
		proj_area_escore	a
	where	nr_seq_regra	= nr_seq_regra_w
	and	a.nr_sequencia	= b.nr_seq_area;

C02 CURSOR FOR
	SELECT	nr_sequencia
	from	proj_avaliacao
	where	nr_seq_modelo	= nr_seq_modelo_avaliacao_w;


BEGIN

begin
select	nr_seq_regra
into STRICT	nr_seq_regra_w
from	proj_escore
where	nr_sequencia	= nr_seq_escore_p;
exception
	when others then
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(279352);
end;

open C01;
loop
fetch C01 into
	nr_seq_area_w,
	nr_seq_item_w,
	qt_escore_w,
	nr_seq_modelo_avaliacao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	count(*)
	into STRICT	qt_registro_w
	from	proj_escore_item
	where	nr_seq_escore	= nr_seq_escore_p
	and	nr_seq_item	= nr_seq_item_w
	and	nr_seq_area	= nr_seq_area_w;

	if (qt_registro_w	= 0) then
		select	nextval('proj_escore_item_seq')
		into STRICT	nr_seq_proj_escore_item_w
		;

		insert into proj_escore_item(
			nr_sequencia, nr_seq_escore, dt_atualizacao,
			nm_usuario, dt_atualizacao_nrec, nm_usuario_nrec,
			nr_seq_item, nr_seq_area, qt_escore)
		values (	nr_seq_proj_escore_item_w, nr_seq_escore_p, clock_timestamp(),
			nm_usuario_p, clock_timestamp(), nm_usuario_p,
			nr_seq_item_w, nr_seq_area_w, qt_escore_w);
	end if;
	if (nr_seq_modelo_avaliacao_w	> 0) then /* Gerar as avaliações do item */
		CALL proj_gerar_avaliacao_quesito(nr_seq_escore_p, nm_usuario_p);
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
