CREATE OR REPLACE PROCEDURE proj_gerar_itens_posic ( nr_seq_proj_p bigint, nm_usuario_p text, dt_referencia_p timestamp) AS $body$
DECLARE


nr_seq_tipo_posic_w	bigint;
nr_seq_item_w		bigint;
ie_status_w		varchar(15);

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	proj_tipo_posic_item
	where	nr_seq_tipo_posic = nr_seq_tipo_posic_w
	and	coalesce(ie_status,ie_status_w) = ie_status_w;


BEGIN

select	max(a.nr_sequencia),
	max(b.ie_status)
into STRICT	nr_seq_tipo_posic_w,
	ie_status_w
from	proj_tipo_posicionamento a,
	proj_projeto b
where	a.nr_seq_classif = b.nr_seq_classif
and	b.nr_sequencia = nr_seq_proj_p;

open C01;
loop
fetch C01 into
	nr_seq_item_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	insert into proj_posicao_coordenacao(nr_sequencia,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					dt_posicao,
					dt_liberacao,
					ds_posicao,
					nr_seq_posic_item,
					nr_seq_proj,
					ie_importante,
					ie_ignorar)
				values (nextval('proj_posicao_coordenacao_seq'),
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					dt_referencia_p,
					null,
					' ',
					nr_seq_item_w,
					nr_seq_proj_p,
					'N',
					'N');

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
