CREATE OR REPLACE PROCEDURE proj_gerar_predecessoras ( nr_seq_cronograma_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_interno_w	bigint;
nr_predecessora_w	bigint;

C01 CURSOR FOR
SELECT	nr_seq_interno
from	proj_cron_etapa
where	nr_seq_cronograma = nr_seq_cronograma_p
and	ie_fase = 'N'
and	(nr_seq_interno IS NOT NULL AND nr_seq_interno::text <> '')
and	ie_regra_ini_fim = 'B'
order by	1;


BEGIN
update	proj_cron_etapa
set	nr_predecessora  = NULL
where	nr_seq_cronograma = nr_seq_cronograma_p;
commit;

open C01;
loop
fetch C01 into
	nr_seq_interno_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	max(nr_seq_interno)
	into STRICT	nr_predecessora_w
	from	proj_cron_etapa
	where	nr_seq_cronograma = nr_seq_cronograma_p
	and	ie_fase = 'N'
	and	(nr_seq_interno IS NOT NULL AND nr_seq_interno::text <> '')
	and	nr_seq_interno < nr_seq_interno_w;

	update	proj_cron_etapa
	set	nr_predecessora = nr_predecessora_w
	where	nr_seq_interno = nr_seq_interno_w
	and	nr_seq_cronograma = nr_seq_cronograma_p;
	commit;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
