CREATE OR REPLACE PROCEDURE proj_gerar_rot_item ( nr_sequencia_p bigint, nr_seq_roteiro_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_item_w		bigint;
nr_sequencia_w		bigint;

c01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	proj_tp_rot_item a
	where	a.ie_situacao = 'A'
	and	a.nr_seq_roteiro = nr_seq_roteiro_p
	and	not exists (SELECT 1
			from 	proj_tp_cli_rot_item x
			where	x.nr_seq_rot_cli	= nr_sequencia_p
			and	x.nr_seq_item		= a.nr_sequencia)
	order by a.nr_seq_apres;


BEGIN

open c01;
loop
Fetch c01 into
	nr_seq_item_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	select	nextval('proj_tp_cli_rot_item_seq')
	into STRICT	nr_sequencia_w
	;

	insert into proj_tp_cli_rot_item(
		nr_sequencia,
		nr_seq_rot_cli,
		dt_atualizacao,
		nm_usuario,
		nr_seq_item,
		ie_resultado)
	values (	nr_sequencia_w,
		nr_sequencia_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_item_w,
		null);
	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
