CREATE OR REPLACE PROCEDURE proj_gerar_score_qual_os ( nr_seq_projeto_p bigint, nr_seq_item_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_retorno_w		bigint;
nr_seq_ordem_w		bigint;
qt_function_w		bigint;
qt_total_os_w		bigint;
ds_resultado_w		bigint;

C01 CURSOR FOR
	SELECT	a.nr_Sequencia
	from	man_ordem_servico a,
		proj_projeto b
	where	a.nr_Sequencia = b.nr_seq_ordem_serv
	and	b.nr_sequencia = nr_seq_projeto_p;

BEGIN

select	count(*)
into STRICT	qt_total_os_w
from	man_ordem_servico a,
	proj_projeto b
where	a.nr_Sequencia = b.nr_seq_ordem_serv
and	b.nr_sequencia = nr_seq_projeto_p;

qt_retorno_w := 0;

open C01;
loop
fetch C01 into
	nr_seq_ordem_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	qt_function_w := (obter_qt_volta_os(nr_seq_ordem_w))::numeric;
	qt_retorno_w := ((qt_retorno_w + qt_function_w) / qt_total_os_w);

	if (qt_retorno_w <= 3) then
		ds_resultado_w := 100;
		elsif (qt_retorno_w > 3) and (qt_retorno_w < 6) then
			ds_resultado_w := 50;
		elsif (qt_retorno_w > 6) then
			ds_resultado_w := 0;
	end if;
	end;
end loop;
close C01;

update 	proj_escore_item
set	qt_escore_real = ds_resultado_w
where	nr_sequencia = nr_seq_item_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
