CREATE OR REPLACE PROCEDURE proj_gerar_seq_interno_cron (nr_seq_cronograma_p bigint) AS $body$
DECLARE



nr_seq_cron_etapa_w	bigint;
nr_seq_cronograma_w	bigint;
nr_seq_interno_w	bigint;
cd_classificacao_w	varchar(255);


C01 CURSOR FOR
	SELECT	nr_sequencia,
		nr_seq_interno,
		nr_seq_cronograma,
		cd_classificacao
	from	proj_cron_etapa
	where	nr_seq_cronograma = nr_seq_cronograma_p
	order by cd_classificacao, nr_sequencia;


BEGIN

update	proj_cron_etapa
set	nr_seq_interno  = NULL
where	nr_seq_cronograma = nr_seq_cronograma_p;
commit;

open C01;
loop
fetch C01 into
	nr_seq_cron_etapa_w,
	nr_seq_interno_w,
	nr_seq_cronograma_w,
	cd_classificacao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	if (coalesce(nr_seq_interno_w::text, '') = '') then

		update	proj_cron_etapa
		set	nr_seq_interno = (SELECT coalesce(max(nr_seq_interno),0)+1
					from	proj_cron_etapa
					where	nr_seq_cronograma = nr_seq_cronograma_w)
		where	nr_sequencia = nr_seq_cron_etapa_w;

		commit;
	end if;

	end;
end loop;
close C01;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
