SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE proj_gerenciar_complexidade (nr_sequencia_p bigint, ie_acao_p text) AS $body$
DECLARE


/*
ie_acao_p:
L - Liberar
E - Estornar liberação
*/
BEGIN

if (ie_acao_p = 'L') then

	update	PROJ_COMPLEXIDADE
	set	dt_liberacao	= clock_timestamp()
	where	nr_sequencia	= nr_sequencia_p
	and	coalesce(dt_liberacao::text, '') = '';

elsif (ie_acao_p = 'E') then

	update	PROJ_COMPLEXIDADE
	set	dt_liberacao	 = NULL
	where	nr_sequencia	= nr_sequencia_p
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '');

end if;

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
