CREATE OR REPLACE PROCEDURE proj_import_cron_etapa ( nr_seq_cronograma_p bigint, ds_atividade_p text, cd_classificacao_p text, dt_inicio_prev_p text, dt_fim_prev_p text, ie_fase_p bigint, qt_hora_prev_p text, nr_seq_etapa_p text, cd_funcao_p text, nm_usuario_p text, nr_seq_id_p bigint, nr_seq_uid_p bigint, ie_regra_ini_fim_p bigint, nr_predecessora_p text, ds_etapa_p text default '', ie_milestone_p text default null, ie_checkpoint_p text default null, nr_seq_area_fase_p bigint default null, dt_ini_prev_p timestamp default null, dt_final_prev_p timestamp default null, nr_seq_etapa_out_p INOUT bigint DEFAULT NULL) AS $body$
DECLARE


dt_inicio_prev_w	timestamp;
dt_fim_prev_w		timestamp;
ie_fase_w		varchar(1);
qt_hora_prev_w		double precision;
qt_nivel_w		bigint;
nr_seq_superior_w	bigint;
nr_seq_apres_w		bigint;
nr_seq_etapa_w		bigint;
ie_modulo_w		varchar(01);
cd_funcao_w		integer;
nr_predecessora_w	bigint;
ie_regra_ini_fim_w	varchar(1);
qt_funcao_w		bigint;
qt_etapa_w		bigint;
nr_seq_etapa_out_w	bigint;
			

BEGIN

if (ie_regra_ini_fim_p = 2) then
	ie_regra_ini_fim_w := 'D';
else
	ie_regra_ini_fim_w := 'B';
end if;

select	CASE WHEN ie_fase_p=1 THEN 'S'  ELSE 'N' END ,
	coalesce(proj_converte_hora_tempo_cron(qt_hora_prev_p),0),
	CTB_Obter_Nivel_Classif_Conta(cd_classificacao_p),
	'N'
into STRICT ie_fase_w,
	qt_hora_prev_w,
	qt_nivel_w,
	ie_modulo_w
;

if (dt_inicio_prev_p IS NOT NULL AND dt_inicio_prev_p::text <> '') and (dt_fim_prev_p IS NOT NULL AND dt_fim_prev_p::text <> '') then
	select to_date(substr(dt_inicio_prev_p,1,10) || ' ' ||
		substr(dt_inicio_prev_p,12,19),'yyyy-mm-dd hh24:mi:ss'),
	to_date(substr(dt_fim_prev_p,1,10) || ' ' ||
		substr(dt_fim_prev_p,12,19),'yyyy-mm-dd hh24:mi:ss')
	into STRICT	
		dt_inicio_prev_w,
		dt_fim_prev_w
	;	
end if;

if (dt_ini_prev_p IS NOT NULL AND dt_ini_prev_p::text <> '') then
	dt_inicio_prev_w := dt_ini_prev_p;
end if;

if (dt_final_prev_p IS NOT NULL AND dt_final_prev_p::text <> '') then
	dt_fim_prev_w := dt_final_prev_p;
end if;


if (qt_nivel_w = 1) then
	nr_seq_superior_w	:= null;
elsif (qt_nivel_w = 2) then
	select	max(a.nr_sequencia)
	into STRICT	nr_seq_superior_w
	from	proj_cron_etapa a
	where	a.nr_seq_cronograma = nr_seq_cronograma_p
	and	ctb_obter_nivel_classif_conta(a.cd_classificacao) = 1;
elsif (qt_nivel_w >= 3) then
	select	max(a.nr_sequencia)
	into STRICT	nr_seq_superior_w
	from	proj_cron_etapa a
	where	a.nr_seq_cronograma = nr_seq_cronograma_p
	and	a.cd_classificacao = substr(proj_etapa_superior_classif(cd_classificacao_p),1,255);
end if;

select	coalesce(max(nr_seq_apres),0) + 10
into STRICT	nr_seq_apres_w
from	proj_cron_etapa
where	CTB_Obter_Nivel_Classif_Conta(cd_classificacao) = qt_nivel_w
and	nr_seq_cronograma		= nr_seq_cronograma_p
and	nr_seq_superior		= nr_seq_superior_w;

nr_seq_etapa_w	:= '';
cd_funcao_w	:= '';

/*	lfrfranca 05/092013 - Esta ocorrendo erro no momento de importacao do projetct versao 2013. Com isso retirei a restricao para que possa importar normalmente.
if	(nvl(nr_seq_etapa_p,'X') <> 'X') then
	nr_seq_etapa_w	:= somente_numero(nr_seq_etapa_p);
	
	select	count(*) 
	into	qt_etapa_w
	from	proj_etapa_cronograma
	where	nr_sequencia = nr_seq_etapa_w;
	
	if ((nr_seq_etapa_w = 0) or (qt_etapa_w = 0)) then
		nr_seq_etapa_w := null;	
		
	end if;
	ie_modulo_w	:= 'S';
end if;

if	(nvl(cd_funcao_p,'X') <> 'X') then	-- lfrfranca (retirado por erro devido ao project)
	cd_funcao_w	:= somente_numero(cd_funcao_p);
	
	select	count(*)
	into	qt_funcao_w
	from	funcao
	where	cd_funcao = cd_funcao_w;
	
	if (qt_funcao_w = 0) then
	cd_funcao_w := '';
	end if;
end if;*/
if (nr_predecessora_p IS NOT NULL AND nr_predecessora_p::text <> '') and (position(';' in nr_predecessora_p) > 0) then
	select	substr(nr_predecessora_p,1,position(';' in nr_predecessora_p)-1)
	into STRICT	nr_predecessora_w
	;
else
	nr_predecessora_w := nr_predecessora_p;
end if;

select	max(nr_id_project)
into STRICT	nr_predecessora_w
from	w_proj_cron_project
where	nr_uid_project = nr_predecessora_w;

select	nextval('proj_cron_etapa_seq')
into STRICT	nr_seq_etapa_out_w
;

nr_seq_etapa_out_p := nr_seq_etapa_out_w;

insert into proj_cron_etapa(
	nr_sequencia,
	nr_seq_cronograma,
	dt_atualizacao,
	nm_usuario,
	ds_atividade,
	cd_classificacao,
	dt_inicio_prev,
	dt_fim_prev,
	ie_fase,
	ie_modulo,
	nr_seq_apres,
	qt_hora_prev,
	pr_etapa,
	nr_seq_superior,
	nr_seq_etapa,
	cd_funcao,
	nr_seq_interno,
	nr_predecessora,
	pr_consultoria,
	ie_regra_ini_fim,
	ds_etapa,
	ie_milestone,
	ie_checkpoint,
	nr_seq_area_fase)
values (nr_seq_etapa_out_w,
	nr_seq_cronograma_p,
	clock_timestamp(),
	nm_usuario_p,
	ds_atividade_p,
	cd_classificacao_p,
	dt_inicio_prev_w,
	dt_fim_prev_w,
	ie_fase_w,
	ie_modulo_w,
	nr_seq_apres_w,
	qt_hora_prev_w,
	0,
	nr_seq_superior_w,
	nr_seq_etapa_w,
	cd_funcao_w,
	nr_seq_id_p,
	coalesce(nr_predecessora_w,CASE WHEN nr_predecessora_p=0 THEN null  ELSE nr_predecessora_p END ),
	0,
	ie_regra_ini_fim_w,
	ds_etapa_p,
	ie_milestone_p,
	ie_checkpoint_p,
	CASE WHEN coalesce(nr_seq_area_fase_p, 0)=0 THEN  null  ELSE nr_seq_area_fase_p END );
	
insert into w_proj_cron_project(nr_id_project,
				nr_uid_project)
			values (nr_seq_id_p,
				nr_seq_uid_p);
commit;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
