SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE proj_import_predecessors ( nr_seq_cronograma_p bigint, nr_seq_id_p bigint, nr_predecessoras_p text, nm_usuario_p text ) AS $body$
DECLARE


nr_seq_etapa_w			bigint;
nr_predecessor_w		bigint;WITH RECURSIVE cte AS (


c_predecessors CURSOR FOR
SELECT	trim(both regexp_substr(substr(nr_predecessoras_p, 1, 2000), '[^;]+', 1, level)) nr_predecessor

instr(substr(nr_predecessoras_p, 1, 2000), ';', 1, level - 1) > 0  UNION ALL


c_predecessors CURSOR FOR
SELECT	trim(both regexp_substr(substr(nr_predecessoras_p, 1, 2000), '[^;]+', 1, level)) nr_predecessor

instr(substr(nr_predecessoras_p, 1, 2000), ';', 1, level - 1) > 0 JOIN cte c ON ()

) SELECT * FROM cte;
;

BEGIN

if (nr_predecessoras_p IS NOT NULL AND nr_predecessoras_p::text <> '') then

	select	max(nr_sequencia)
	into STRICT	nr_seq_etapa_w
	from	proj_cron_etapa
	where	nr_seq_cronograma = nr_seq_cronograma_p
	and		nr_seq_interno = nr_seq_id_p;

	if (nr_seq_etapa_w IS NOT NULL AND nr_seq_etapa_w::text <> '') then

		for r_c_predecessors in c_predecessors loop

			select	max(nr_sequencia)
			into STRICT	nr_predecessor_w
			from	proj_cron_etapa
			where	nr_seq_cronograma = nr_seq_cronograma_p
			and		nr_seq_interno = (	SELECT	nr_id_project
										from	w_proj_cron_project
										where	nr_uid_project = r_c_predecessors.nr_predecessor
									);

			if (nr_predecessor_w IS NOT NULL AND nr_predecessor_w::text <> '') then

				insert into proj_cron_predec(
					nr_sequencia,
					dt_atualizacao,
					nm_usuario,
					dt_atualizacao_nrec,
					nm_usuario_nrec,
					nr_seq_etapa_atual,
					nr_seq_etapa_predec)
				values (
					nextval('proj_cron_predec_seq'),
					clock_timestamp(),
					nm_usuario_p,
					clock_timestamp(),
					nm_usuario_p,
					nr_seq_etapa_w,
					nr_predecessor_w);
			end if;
		end loop;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
