SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE proj_inserir_modulo_gestao_con ( ds_modulo_p text, nr_seq_consultor_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		bigint;
ds_lista_modulo_w	varchar(800);
tam_lista_w		bigint;
ie_pos_virgula_w	smallint	:= 0;
cd_modulo_w		bigint;
ie_tipo_consultor_w	varchar(5);


BEGIN
select	max(CASE WHEN coalesce(ie_tipo_consultor,'A')='CO' THEN 'O' WHEN coalesce(ie_tipo_consultor,'A')='CP' THEN 'P'  ELSE 'A' END )
into STRICT	ie_tipo_consultor_w
from	com_canal_consultor
where	nr_sequencia = nr_seq_consultor_p;

ds_lista_modulo_w	:= ds_modulo_p;
if (ds_lista_modulo_w IS NOT NULL AND ds_lista_modulo_w::text <> '') then
	begin
	WHILE(ds_lista_modulo_w IS NOT NULL AND ds_lista_modulo_w::text <> '') LOOP
		begin
		tam_lista_w			:= length(ds_lista_modulo_w);
		ie_pos_virgula_w			:= position(',' in ds_lista_modulo_w);

		if (ie_pos_virgula_w <> 0) then
			cd_modulo_w		:= substr(ds_lista_modulo_w,1,(ie_pos_virgula_w - 1));
			ds_lista_modulo_w		:= substr(ds_lista_modulo_w, (ie_pos_virgula_w + 1), tam_lista_w);
		end if;

		select	nextval('com_cons_gest_con_fun_seq')
		into STRICT	nr_sequencia_w
		;

		insert	into com_cons_gest_con_mod(	nr_sequencia,
							nr_seq_consultor,
							nr_seq_mod_impl,
							nr_seq_etapa,
							dt_atualizacao,
							nm_usuario,
							dt_atualizacao_nrec,
							nm_usuario_nrec)
						values (	nr_sequencia_w,
							nr_seq_consultor_p,
							cd_modulo_w,
							null,
							clock_timestamp(),
							nm_usuario_p,
							clock_timestamp(),
							nm_usuario_p);

		CALL com_gerar_funcao_conhecimento(	nr_sequencia_w,
						cd_modulo_w,
						nm_usuario_p,
						ie_tipo_consultor_w);
		end;
	END loop;
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
