SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE proj_inserir_req_ofic_uso ( nr_seq_ofic_uso_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_des_req_item_w		bigint;
nr_seq_projeto_w		bigint;

c01 CURSOR FOR
SELECT	b.nr_sequencia
from	des_requisito_item b,
	des_requisito a
where	b.nr_seq_requisito	= a.nr_sequencia
and	a.nr_seq_projeto	= nr_seq_projeto_w
and	b.ie_status		in ('A','C','T')
and	not exists (select	1
			from	proj_des_ofic_uso y,
				proj_ofic_requisito x
			where	x.nr_seq_oficializacao	= y.nr_sequencia
			and	y.nr_seq_proj	= a.nr_seq_projeto
			and	x.nr_seq_des_req_item = b.nr_sequencia);


BEGIN

if (nr_seq_ofic_uso_p IS NOT NULL AND nr_seq_ofic_uso_p::text <> '') then
	select	a.nr_seq_proj
	into STRICT	nr_seq_projeto_w
	from	proj_des_ofic_uso a
	where	a.nr_sequencia	= nr_seq_ofic_uso_p;

	open c01;
	loop
	fetch c01 into
		nr_seq_des_req_item_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		insert into proj_ofic_requisito(nr_sequencia,
			nm_usuario,
			dt_atualizacao,
			nm_usuario_nrec,
			dt_atualizacao_nrec,
			nr_seq_oficializacao,
			nr_seq_des_req_item)
		values (nextval('proj_ofic_requisito_seq'),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nr_seq_ofic_uso_p,
			nr_seq_des_req_item_w);
		end;
	end loop;
	close c01;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
