SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE proj_liberar_escore ( nr_seq_escore_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_inconsistencia_w bigint;

BEGIN

select 	count(1)
into STRICT 	qt_inconsistencia_w
from 	proj_escore pe,
	proj_escore_item pei
where 	pei.nr_seq_escore = pe.nr_sequencia
and 	pe.nr_sequencia = nr_seq_escore_p
and 	pei.nr_seq_area not in (SELECT pae.nr_sequencia
				from proj_area_escore pae
				where pe.nr_seq_regra = pae.nr_seq_regra
				and pae.ie_situacao = 'A');

if (qt_inconsistencia_w > 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(1099570);
end if;
				
update	proj_escore
set	dt_liberacao	= clock_timestamp(),
	nm_usuario_lib	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp(),
	nm_usuario	= nm_usuario_p
where	nr_sequencia	= nr_seq_escore_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
