CREATE OR REPLACE PROCEDURE proj_liberar_planej_nivel ( nr_sequencia_p bigint, ie_acao_p text, nm_usuario_p text ) AS $body$
BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	begin
	if (ie_acao_p = 'L') then
		update	proj_consult_nivel_planej
		set	dt_liberacao  = clock_timestamp(),
			nm_usuario    = nm_usuario_p
		where	nr_sequencia  = nr_sequencia_p;
	end if;

	if (ie_acao_p = 'D') then
		update	proj_consult_nivel_planej
		set	dt_liberacao   = NULL,
			nm_usuario    = nm_usuario_p
		where	nr_sequencia  = nr_sequencia_p;
	end if;
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
