CREATE OR REPLACE PROCEDURE proj_lic_atual_dtlib ( nr_sequencia_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
BEGIN
	if (ie_opcao_p = 'L') then
	begin
		update 	proj_lic_aprendida
		set	dt_liberacao = clock_timestamp(),
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
		where 	nr_sequencia = nr_sequencia_p;
	end;
	elsif (ie_opcao_p = 'E') then
	begin
		update 	proj_lic_aprendida
		set	dt_liberacao  = NULL,
			nm_usuario = nm_usuario_p,
			dt_atualizacao = clock_timestamp()
		where 	nr_sequencia = nr_sequencia_p;
	end;
end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
