CREATE OR REPLACE PROCEDURE proj_log_ultimo_recalc ( nr_seq_cronograma_p proj_cronograma.nr_sequencia%type, ie_origem_p text, ie_operacao_p text, nm_usuario_p usuario.nm_usuario%type) AS $body$
DECLARE


/*
	++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
	Objective: Generate a log that shows the last time a user changed a
		data that needs the schedule recalculation
	------------------------------------------------------------------------
	Direct call: 
	[ X ] Dictionary objects
	[   ] Tasy (Delphi/Java/HTML5)
	[   ] Portal
	[   ] Reports
	[   ] Others:
	 -----------------------------------------------------------------------
