CREATE OR REPLACE PROCEDURE proj_obter_info_equipe_papel ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nm_pessoa_fisica_w	varchar(80);
ds_setor_w		varchar(80);
ds_fone_w		varchar(40);
ds_email_w		varchar(255);
cd_pessoa_fisica_w	varchar(20);


BEGIN 
 
select	max(cd_pessoa_fisica) 
into STRICT	cd_pessoa_fisica_w 
from	proj_equipe_papel 
where	nr_sequencia	= nr_sequencia_p;
 
if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then 
 
	select	substr(obter_nome_pf(a.cd_pessoa_fisica),1,80), 
		b.ds_setor_trabalho, 
		b.nr_telefone, 
		b.ds_email 
	into STRICT	nm_pessoa_fisica_w, 
		ds_setor_w, 
		ds_fone_w, 
		ds_email_w 
	from	compl_pessoa_fisica b, 
		pessoa_fisica a 
	where	a.cd_pessoa_fisica	= cd_pessoa_fisica_w 
	and	a.cd_pessoa_fisica	= b.cd_pessoa_fisica 
	and	ie_tipo_complemento	= 1;
 
	update	proj_equipe_papel 
	set	nm_pessoa		= nm_pessoa_fisica_w, 
		ds_setor		= ds_setor_w, 
		ds_fone			= ds_fone_w, 
		ds_email		= ds_email_w, 
		nm_usuario		= nm_usuario_p, 
		dt_atualizacao		= clock_timestamp() 
	where	nr_sequencia		= nr_sequencia_p;
 
	commit;
end if;
	 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
