CREATE OR REPLACE PROCEDURE proj_previsao_nota_rat ( nr_seq_rat_mes_p bigint, dt_previsto_nf_p timestamp, nm_usuario_p text) AS $body$
BEGIN

if (trunc(dt_previsto_nf_p,'dd') between	trunc(clock_timestamp(),'dd') and trunc(clock_timestamp() + interval '15 days','dd')) then
	update	proj_rat_mes
	set	dt_previsto_nf	= dt_previsto_nf_p,
		nm_usuario	= nm_usuario_p,
		dt_atualizacao	= clock_timestamp()
	where	nr_sequencia	= nr_seq_rat_mes_p;
else
	CALL wheb_mensagem_pck.exibir_mensagem_abort(265814,'sysdate= '||trunc(clock_timestamp(),'dd')||'trunc='||trunc(clock_timestamp() + interval '15 days','dd'));
	--Mensagem: A data informada deve estar entre ' || trunc(sysdate,'dd') || ' e ' || trunc(sysdate+15,'dd') ||
end if;

commit;

end;
$body$
