CREATE OR REPLACE PROCEDURE proj_recalcular_ativ_cron ( nr_seq_cronograma_p bigint, nm_usuario_p text) AS $body$
DECLARE


c01 CURSOR FOR
	SELECT	nr_sequencia
	from	proj_cron_etapa
	where	nr_seq_cronograma = nr_seq_cronograma_p
	and	coalesce(ie_situacao,'A') = 'A';
	/* OS 147594
	select	distinct
		nr_seq_etapa_cron
	from	proj_rat_ativ a,
		proj_rat b
	where	a.nr_seq_etapa_cron in (
				select	nr_sequencia
				from	proj_cron_etapa
				where	nr_seq_cronograma = nr_seq_cronograma_p)
	and	a.nr_seq_rat = b.nr_sequencia;*/
nr_seq_etapa_w bigint;


BEGIN

open c01;
loop
	fetch c01 into
		nr_seq_etapa_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */

	begin

 	CALL Atualizar_horas_cronograma(nr_seq_etapa_w);
	CALL Atualizar_Horas_Etapa_Cron(nr_seq_etapa_w);
	CALL proj_atualizar_pr_realizada(nr_seq_cronograma_p, nr_seq_etapa_w, 0);
	CALL Gerar_Horario_Inicial_Etapa(nr_seq_etapa_w, 'I');

 	end;
end loop;
close c01;

CALL Proj_Recalcula_Cronograma(nr_seq_cronograma_p);
CALL atualizar_total_horas_cron(nr_seq_cronograma_p);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
