CREATE OR REPLACE PROCEDURE proj_recalcular_tempo_cron ( nr_seq_cron_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_etapa_w	bigint;
nr_seq_pai_w	bigint;

c01 CURSOR FOR
SELECT	nr_sequencia,
	nr_seq_superior
from	proj_cron_etapa
where	nr_seq_cronograma = nr_seq_cron_p
and	ie_fase = 'N'
and	coalesce(ie_situacao,'A') = 'A'
order by
	cd_classificacao desc;


BEGIN
if (nr_seq_cron_p IS NOT NULL AND nr_seq_cron_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	open c01;
	loop
	fetch c01 into 	nr_seq_etapa_w,
			nr_seq_pai_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		CALL atualizar_horas_etapa_cron(nr_seq_etapa_w);
		CALL atualizar_horas_etapa_cron(nr_seq_pai_w);
		end;
	end loop;
	close c01;
	CALL atualizar_total_horas_cron(nr_seq_cron_p);
	CALL proj_desenv_atual_perc_cron(nr_seq_cron_p, nm_usuario_p);
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
