SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE proj_reimport_cron_etapa ( nr_seq_cronograma_p bigint, ds_atividade_p text, cd_classificacao_p text, dt_inicio_prev_p text, dt_fim_prev_p text, ie_fase_p bigint, qt_hora_prev_p text, nr_seq_etapa_p text, cd_funcao_p text, nm_usuario_p text, nr_seq_id_p bigint, nr_seq_uid_p bigint, ie_regra_ini_fim_p bigint, nr_predecessora_p text, ds_etapa_p text default '', nr_seq_etapa_out_p INOUT bigint DEFAULT NULL) AS $body$
DECLARE


dt_inicio_prev_w	timestamp;
dt_fim_prev_w		timestamp;
ie_fase_w		varchar(1);
qt_hora_prev_w		double precision;
qt_nivel_w		bigint;
nr_seq_superior_w	bigint;
nr_seq_apres_w		bigint;
nr_seq_etapa_w		bigint;
ie_modulo_w		varchar(01);
cd_funcao_w		integer;
nr_predecessora_w	bigint;
ie_regra_ini_fim_w	varchar(1);
qt_funcao_w		bigint;
qt_etapa_w		bigint;
			

BEGIN

if (ie_regra_ini_fim_p = 2) then
	ie_regra_ini_fim_w := 'D';
else
	ie_regra_ini_fim_w := 'B';
end if;

select	to_date(substr(dt_inicio_prev_p,1,10) || ' ' ||
		substr(dt_inicio_prev_p,12,19),'yyyy-mm-dd hh24:mi:ss'),
	to_date(substr(dt_fim_prev_p,1,10) || ' ' ||
		substr(dt_fim_prev_p,12,19),'yyyy-mm-dd hh24:mi:ss'),
	CASE WHEN ie_fase_p=1 THEN 'S'  ELSE 'N' END ,
	coalesce(proj_converte_hora_tempo_cron(qt_hora_prev_p),0),
	CTB_Obter_Nivel_Classif_Conta(cd_classificacao_p),
	'N'
into STRICT	dt_inicio_prev_w,
	dt_fim_prev_w,
	ie_fase_w,
	qt_hora_prev_w,
	qt_nivel_w,
	ie_modulo_w
;

if (qt_nivel_w = 1) then
	nr_seq_superior_w	:= null;
elsif (qt_nivel_w = 2) then
	select	max(a.nr_sequencia)
	into STRICT	nr_seq_superior_w
	from	proj_cron_etapa a
	where	a.nr_seq_cronograma = nr_seq_cronograma_p
	and	ctb_obter_nivel_classif_conta(a.cd_classificacao) = 1;
elsif (qt_nivel_w >= 3) then
	select	max(a.nr_sequencia)
	into STRICT	nr_seq_superior_w
	from	proj_cron_etapa a
	where	a.nr_seq_cronograma = nr_seq_cronograma_p
	and	a.cd_classificacao = substr(proj_etapa_superior_classif(cd_classificacao_p),1,255);
end if;

select	coalesce(max(nr_seq_apres),0) + 10
into STRICT	nr_seq_apres_w
from	proj_cron_etapa
where	CTB_Obter_Nivel_Classif_Conta(cd_classificacao) = qt_nivel_w
and	nr_seq_cronograma		= nr_seq_cronograma_p
and	nr_seq_superior		= nr_seq_superior_w;

nr_seq_etapa_w	:= '';
cd_funcao_w	:= '';

if (nr_predecessora_p IS NOT NULL AND nr_predecessora_p::text <> '') and (position(';' in nr_predecessora_p) > 0) then
	select	substr(nr_predecessora_p,1,position(';' in nr_predecessora_p)-1)
	into STRICT	nr_predecessora_w
	;
else
	nr_predecessora_w := nr_predecessora_p;
end if;

select	max(nr_id_project)
into STRICT	nr_predecessora_w
from	w_proj_cron_project
where	nr_uid_project = nr_predecessora_w;

update	proj_cron_etapa
set		dt_atualizacao = clock_timestamp(),
		nm_usuario = nm_usuario_p,
		ds_atividade = ds_atividade_p,
		dt_atualizacao_nrec = clock_timestamp(),
		nm_usuario_nrec = nm_usuario_p,
		cd_classificacao = cd_classificacao_p,
		dt_inicio_prev = dt_inicio_prev_w,
		dt_fim_prev = dt_fim_prev_w,
		ie_fase = ie_fase_w,
		ie_modulo = ie_modulo_w,
		nr_seq_apres = nr_seq_apres_w,
		qt_hora_prev = qt_hora_prev_w,
		pr_etapa = 0,
		nr_seq_superior = nr_seq_superior_w,
		nr_seq_etapa = nr_seq_etapa_w,
		cd_funcao = cd_funcao_w,
		nr_seq_interno = nr_seq_id_p,
		nr_predecessora = coalesce(nr_predecessora_w,CASE WHEN nr_predecessora_p=0 THEN null  ELSE nr_predecessora_p END ),
		pr_consultoria = 0,
		ie_regra_ini_fim = ie_regra_ini_fim_w,
		ds_etapa = ds_etapa_p,									
		DT_INICIO_REAL  = NULL,
		DT_FIM_REAL  = NULL,
		QT_HORA_REAL  = NULL,
		QT_HORA_SALDO  = NULL,
		DT_CADASTRO  = NULL,
		DT_FINAL_MULTIPLIC  = NULL,
		DT_PILOTO  = NULL,
		DT_ATIVACAO  = NULL,
		NR_SEQ_ESTRUTURA  = NULL,
		NR_SEQ_ORDENACAO  = NULL,		
		IE_ETAPA_EXEC_DESENV  = NULL,
		IE_MPO  = NULL,
		IE_ADERENCIA  = NULL,
		IE_CADASTROS  = NULL,
		IE_PARAMETRIZACAO  = NULL,
		IE_TREINAMENTO  = NULL,
		IE_TESTE_PILOTO  = NULL,
		PR_PREVISTO  = NULL,
		IE_OBRIGATORIO  = NULL,
		IE_TIPO_OBJ_PROJ_MIGR  = NULL,
		IE_ATIVIDADE_ADICIONAL  = NULL,
		IE_SITUACAO  = NULL,
		IE_TIPO_ATIV_MET  = NULL,
		DT_LIBERACAO_ETAPA  = NULL,
		QT_HORAS_ETAPA_ADIC  = NULL,
		IE_MILESTONE  = NULL,
		PR_PREV_ETAPA  = NULL,
		IE_VIRADA  = NULL,
		NR_SEQ_SUB_PROJ  = NULL,
		DT_ACORDO_EXTERNO  = NULL,
		IE_PRODUTO  = NULL,
		IE_GERA_OS  = NULL,
		NR_SEQ_PROCESSO_ETAPA  = NULL,
		IE_STATUS_ETAPA  = NULL,
		NR_SEQ_ETAPA_NPI  = NULL,
		QT_DIAS  = NULL,
		IE_RECEITA_RECONHECIDA  = NULL		
where 	nr_sequencia = nr_seq_etapa_p
and		nr_seq_cronograma = nr_seq_cronograma_p;

	
insert into w_proj_cron_project(nr_id_project,
				nr_uid_project)
			values (nr_seq_id_p,
				nr_seq_uid_p);
				
insert into w_etapas_importacao values (nextval('w_etapas_importacao_seq'), nm_usuario_p, nr_seq_etapa_p);
				
commit;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE proj_reimport_cron_etapa ( nr_seq_cronograma_p bigint, ds_atividade_p text, cd_classificacao_p text, dt_inicio_prev_p text, dt_fim_prev_p text, ie_fase_p bigint, qt_hora_prev_p text, nr_seq_etapa_p text, cd_funcao_p text, nm_usuario_p text, nr_seq_id_p bigint, nr_seq_uid_p bigint, ie_regra_ini_fim_p bigint, nr_predecessora_p text, ds_etapa_p text default '', nr_seq_etapa_out_p INOUT bigint DEFAULT NULL) FROM PUBLIC;

