SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE proj_tp_calc_aderencia ( nr_seq_item_result_p bigint, nm_usuario_p text ) AS $body$
DECLARE


nr_seq_item_w		bigint;
ie_resultado_w		varchar(3);
pr_grau_importancia_w	double precision;
pr_resultado_w		double precision;
pr_efetividade_w	double precision;
nm_user_w		varchar(1);

C01 CURSOR FOR
SELECT	a.nr_seq_item,
	a.ie_resultado
from	proj_tp_registro_v a
where	a.nr_seq_item_result = nr_seq_item_result_p;


BEGIN

select	coalesce(max('S'), 'N')
into STRICT	nm_user_w

where	obter_se_base_corp = 'S'
or	obter_se_base_wheb = 'S';

open C01;
loop
fetch C01 into	
	nr_seq_item_w,
	ie_resultado_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
		
	select	a.pr_grau_importancia
	into STRICT	pr_grau_importancia_w
	from	proj_tp_rot_item a
	where	a.nr_sequencia = nr_seq_item_w;

	if (coalesce(pr_grau_importancia_w,0) = 0) then
		CALL WHEB_MENSAGEM_PCK.Exibir_Mensagem_Abort(277384);
	end if;

	if (nm_user_w = 'S') then
		begin
			if (ie_resultado_w = 'C') then
				pr_resultado_w := 1;
			elsif (ie_resultado_w = 'CR') then
				pr_resultado_w := 0.8;
			elsif (ie_resultado_w = 'NC') then
				pr_resultado_w := 0;
			end if;
		end;
	else
		begin
			if (ie_resultado_w = 'C') then
				pr_resultado_w := 1;
			elsif (ie_resultado_w = 'CR') then
				pr_resultado_w := 0.5;
			elsif (ie_resultado_w = 'NC') then
				pr_resultado_w := 0;
			end if;
		end;
	end if;
		
	pr_efetividade_w	:= (pr_grau_importancia_w * pr_resultado_w);
	
	update	proj_tp_cli_rot_item
	set	pr_efetividade = pr_efetividade_w,
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_sequencia = nr_seq_item_result_p;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
