CREATE OR REPLACE PROCEDURE proj_vincular_desp_viagem (nr_sequencia_p bigint, nr_seq_fech_proj_p bigint, nm_usuario_p text) AS $body$
DECLARE


vl_total_desp_w		double precision;


BEGIN

update	via_relat_desp
set	nr_seq_fech_proj = nr_seq_fech_proj_p,
	nm_usuario	= nm_usuario_p,
	dt_atualizacao	= clock_timestamp()
where	nr_sequencia	= nr_sequencia_p;

select 	sum(coalesce(obter_valor_desp_viagem(nr_sequencia,'4'),0))
into STRICT	vl_total_desp_w
from 	via_relat_desp
where 	nr_seq_fech_proj = nr_seq_fech_proj_p;

update  proj_desp_viagem_mes
set	vl_total_pagar = vl_total_desp_w
where 	nr_sequencia = nr_seq_fech_proj_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
